do local GeminiLib={};GeminiLib.CreateWindow=function(self,title) local ScreenGui=Instance.new("ScreenGui",game:GetService("CoreGui"));local Main=Instance.new("Frame",ScreenGui);Main.Name="GeminiMain";Main.Size=UDim2.new(0,480,0,350);Main.Position=UDim2.new(0.5, -240,0.5, -175);Main.BackgroundColor3=Color3.fromRGB(12,12,12);Main.BorderSizePixel=0;Instance.new("UICorner",Main).CornerRadius=UDim.new(0,10);local UIS=game:GetService("UserInputService");UIS.InputBegan:Connect(function(input,processed) if ( not processed and (input.KeyCode==Enum.KeyCode.Z)) then Main.Visible= not Main.Visible;end end);local Sidebar=Instance.new("Frame",Main);Sidebar.Size=UDim2.new(0,130,1,0);Sidebar.BackgroundColor3=Color3.fromRGB(18,18,18);Instance.new("UICorner",Sidebar);local TabContainer=Instance.new("Frame",Main);TabContainer.Size=UDim2.new(1, -145,1, -45);TabContainer.Position=UDim2.new(0,140,0,40);TabContainer.BackgroundTransparency=1;local Title=Instance.new("TextLabel",Main);Title.Size=UDim2.new(1, -145,0,35);Title.Position=UDim2.new(0,140,0,5);Title.Text=title;Title.TextColor3=Color3.fromRGB(0,200,255);Title.Font="GothamBold";Title.BackgroundTransparency=1;Title.TextXAlignment="Left";Title.TextSize=18;local dragging,dragStart,startPos;Main.InputBegan:Connect(function(i) if (i.UserInputType==Enum.UserInputType.MouseButton1) then dragging=true;dragStart=i.Position;startPos=Main.Position;end end);game:GetService("UserInputService").InputChanged:Connect(function(i) if (dragging and (i.UserInputType==Enum.UserInputType.MouseMovement)) then local d=i.Position-dragStart ;Main.Position=UDim2.new(startPos.X.Scale,startPos.X.Offset + d.X ,startPos.Y.Scale,startPos.Y.Offset + d.Y );end end);game:GetService("UserInputService").InputEnded:Connect(function(i) if (i.UserInputType==Enum.UserInputType.MouseButton1) then dragging=false;end end);local Lib={};Lib.CreateTab=function(self,name) local Content=Instance.new("ScrollingFrame",TabContainer);Content.Size=UDim2.new(1,0,1,0);Content.BackgroundTransparency=1;Content.Visible=false;Content.ScrollBarThickness=2;Instance.new("UIListLayout",Content).Padding=UDim.new(0,8);local TabBtn=Instance.new("TextButton",Sidebar);TabBtn.Size=UDim2.new(1, -10,0,35);TabBtn.Position=UDim2.new(0,5,0,( #Sidebar:GetChildren() * 40) + 10 );TabBtn.Text=name;TabBtn.BackgroundColor3=Color3.fromRGB(25,25,25);TabBtn.TextColor3=Color3.new(0.8,0.8,0.8);Instance.new("UICorner",TabBtn);TabBtn.MouseButton1Click:Connect(function() for _,v in pairs(TabContainer:GetChildren()) do v.Visible=false;end for _,v in pairs(Sidebar:GetChildren()) do if v:IsA("TextButton") then v.BackgroundColor3=Color3.fromRGB(25,25,25);end end Content.Visible=true;TabBtn.BackgroundColor3=Color3.fromRGB(0,100,150);end);if ( #Sidebar:GetChildren()==1) then Content.Visible=true;TabBtn.BackgroundColor3=Color3.fromRGB(0,100,150);end local Elements={};Elements.AddButton=function(self,text,callback) local b=Instance.new("TextButton",Content);b.Size=UDim2.new(1, -10,0,35);b.Text=text;b.BackgroundColor3=Color3.fromRGB(35,35,35);b.TextColor3=Color3.new(1,1,1);Instance.new("UICorner",b);b.MouseButton1Click:Connect(callback);end;Elements.AddToggle=function(self,text,callback) local t=Instance.new("TextButton",Content);t.Size=UDim2.new(1, -10,0,35);t.Text=text   .. " [OFF]" ;t.BackgroundColor3=Color3.fromRGB(35,35,35);t.TextColor3=Color3.new(1,0.3,0.3);Instance.new("UICorner",t);local s=false;t.MouseButton1Click:Connect(function() s= not s;t.Text=text   .. ((s and " [ON]") or " [OFF]") ;t.TextColor3=(s and Color3.new(0.3,1,0.3)) or Color3.new(1,0.3,0.3) ;callback(s);end);end;Elements.AddSlider=function(self,text,min,max,callback) local sFrame=Instance.new("Frame",Content);sFrame.Size=UDim2.new(1, -10,0,50);sFrame.BackgroundColor3=Color3.fromRGB(35,35,35);Instance.new("UICorner",sFrame);local label=Instance.new("TextLabel",sFrame);label.Size=UDim2.new(1,0,0.5,0);label.Text=text   .. ": "   .. min ;label.BackgroundTransparency=1;label.TextColor3=Color3.new(1,1,1);local sliderBg=Instance.new("Frame",sFrame);sliderBg.Size=UDim2.new(0.9,0,0.15,0);sliderBg.Position=UDim2.new(0.05,0,0.7,0);sliderBg.BackgroundColor3=Color3.fromRGB(20,20,20);local fill=Instance.new("Frame",sliderBg);fill.Size=UDim2.new(0,0,1,0);fill.BackgroundColor3=Color3.fromRGB(0,200,255);sliderBg.InputBegan:Connect(function(i) if (i.UserInputType==Enum.UserInputType.MouseButton1) then local function update() local percent=math.clamp((game:GetService("Players").LocalPlayer:GetMouse().X-sliderBg.AbsolutePosition.X)/sliderBg.AbsoluteSize.X ,0,1);local val=math.floor(min + ((max-min) * percent) );fill.Size=UDim2.new(percent,0,1,0);label.Text=text   .. ": "   .. val ;callback(val);end local move;move=UIS.InputChanged:Connect(function(input) if (input.UserInputType==Enum.UserInputType.MouseMovement) then update();end end);local ended;ended=UIS.InputEnded:Connect(function(input) if (input.UserInputType==Enum.UserInputType.MouseButton1) then move:Disconnect();ended:Disconnect();end end);update();end end);end;Elements.AddInput=function(self,text,placeholder,callback) local iFrame=Instance.new("Frame",Content);iFrame.Size=UDim2.new(1, -10,0,40);iFrame.BackgroundColor3=Color3.fromRGB(35,35,35);Instance.new("UICorner",iFrame);local tb=Instance.new("TextBox",iFrame);tb.Size=UDim2.new(1, -10,1,0);tb.BackgroundTransparency=1;tb.TextColor3=Color3.new(1,1,1);tb.PlaceholderText=placeholder;tb.Text="";tb.FocusLost:Connect(function() callback(tb.Text);end);end;return Elements;end;return Lib;end;local lp=game.Players.LocalPlayer;local SelectedTarget=nil;local ReachEnabled=false;local HitboxSize=2;local KillTime=0.4;local function bypassTP(targetCFrame) if ( not lp.Character or  not lp.Character:FindFirstChild("HumanoidRootPart")) then return;end game:GetService("TweenService"):Create(lp.Character.HumanoidRootPart,TweenInfo.new(0.08),{CFrame=targetCFrame}):Play();end game:GetService("RunService").RenderStepped:Connect(function() if ReachEnabled then for _,v in pairs(game.Players:GetPlayers()) do if ((v~=lp) and v.Character and v.Character:FindFirstChild("HumanoidRootPart")) then local hrp=v.Character.HumanoidRootPart;hrp.Size=Vector3.new(HitboxSize,HitboxSize,HitboxSize);hrp.Transparency=1;hrp.CanCollide=false;end end end end);local Window=GeminiLib:CreateWindow("STEAL INFINITE v3");local Combat=Window:CreateTab("Combat");local ReachTab=Window:CreateTab("Reach");Combat:AddInput("Search Target","Enter username...",function(text) for _,v in pairs(game.Players:GetPlayers()) do if v.Name:lower():find(text:lower()) then SelectedTarget=v;print("Locked: "   .. v.Name );end end end);Combat:AddToggle("Spectate Locked Player",function(s) workspace.CurrentCamera.CameraSubject=(s and SelectedTarget and SelectedTarget.Character.Humanoid) or lp.Character.Humanoid ;end);Combat:AddButton("ðŸ”¥ BYPASS KILL ðŸ”¥",function() if (SelectedTarget and SelectedTarget.Character) then local old=lp.Character.HumanoidRootPart.CFrame;bypassTP(SelectedTarget.Character.HumanoidRootPart.CFrame * CFrame.new(0,0,2) );local tool=lp.Backpack:FindFirstChildOfClass("Tool") or lp.Character:FindFirstChildOfClass("Tool") ;if tool then lp.Character.Humanoid:EquipTool(tool);local start=tick();while (tick() -start)<KillTime  do tool:Activate();if SelectedTarget.Character:FindFirstChild("HumanoidRootPart") then lp.Character.HumanoidRootPart.CFrame=SelectedTarget.Character.HumanoidRootPart.CFrame * CFrame.new(0,0,2) ;end task.wait();end end bypassTP(old);end end);ReachTab:AddToggle("Enable Reach (Hitbox)",function(s) ReachEnabled=s;end);ReachTab:AddSlider("Hitbox Scale",2,25,function(v) HitboxSize=v;end);Combat:AddButton("Instant TP to Target",function() if (SelectedTarget and SelectedTarget.Character) then lp.Character.HumanoidRootPart.CFrame=SelectedTarget.Character.HumanoidRootPart.CFrame * CFrame.new(0,0,3) ;end end); end
